"use strict";

$(document).ready(function () {
  $(".slider").slick({
    dots: !0,
    adaptiveHeight: !0,
    variableWidth: !0,
    autoplay: !0,
    centerMode: !0,
    autoplaySpeed: 800,
    appendArrows: $(".social__bottom-slider"),
    appendDots: $(".social__bottom-slider"),
    responsive: [{
      breakpoint: 576,
      settings: {
        lazyLoad: "ondemand",
        slidesToShow: 1
      }
    }]
  });
}), $(document).ready(function () {
  $(".hamburger").click(function () {
    $(".hamburger, .menu").toggleClass("active"), $("body").toggleClass("lock");
  });
});